mapper.insert:
INSERT INTO t_dm_user
SET idm = #{idm}
, iuser = #{iuser}


    SELECT IFNULL(MAX(seq), 0) + 1
    FROM t_dm_msg
    WHERE idm = #{idm}

INSERT INTO t_dm_msg
SET idm = #{idm}
, seq = #{seq}
, iuser = #{loginedIuser}
, msg = #{msg}

mapper.select:
SELECT seq
FROM t_dm_msg
WHERE idm = #{idm}
AND iuser = #{login}
AND iuser = #{target}

mapper.delete:
DELETE
FROM t_dm_msg
WHERE idm = #{idm}
AND seq = #{seq}
AND iuser = #{iuser}
